sys_script_include.script:
/* global global, gs */
/* global Class, GlideRecordSecure */
/* eslint no-undef: "error" */
var ScriptIncludeClient = Class.create();
ScriptIncludeClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	initialize: function(request, responseXML, gc) {
        global.AbstractAjaxProcessor.prototype.initialize.apply(this, arguments);
    },

	getRecord: function () {
		var result = {
			status: 'error',
			message: ''
		};

		var recordTable = this.getParameter('record_table');
		var recordSysID = this.getParameter('record_sys_id');
		if (recordTable && recordSysID) {
			var recordGr = new GlideRecordSecure(recordTable);
			recordGr.addQuery('sys_id', recordSysID);
			recordGr.setLimit(1);
			recordGr.query();
			if (recordGr.next()) {
				result.record = {
					display_value: recordGr.getDisplayValue()
				};
			} else {
				result.error_message = gs.getMessage('insufficient privileges to perform this action');
			}
		} else {
			result.error_message = gs.getMessage('Invalid Parameters!');
		}

		return JSON.stringify(result);
	},

	validateIncident: function () {
		var result = {
			status: 'error',
			message: ''
		};

		var incidentSysID = this.getParameter('incident');
		if (incidentSysID) {
			var recordGr = new GlideRecordSecure('incident');
			recordGr.addQuery('sys_id', incidentSysID);
			recordGr.setLimit(1);
			recordGr.query();
			if (recordGr.next()) {
				// we would not call a Server Side Script Include which so that we have all the validation in one place
				// security is now critical: The Server Side Script Include should not do any writes, or violate data privacy
				result.valid = true;
			} else {
				result.error_message = gs.getMessage('insufficient privileges to perform this action');
			}
		} else {
			result.error_message = gs.getMessage('Invalid Parameters!');
		}

		return JSON.stringify(result);
	},

    type: 'ScriptIncludeClient'
});
